mapper.select:
SELECT r.id, r.user_id, r.rating, r.content, r.created_at, u.nickname, u.profile_image_url FROM place_review r LEFT JOIN users u ON r.user_id = u.user_id
r.place_name = #{placeName}
AND r.region = #{region}
ORDER BY r.created_at DESC

SELECT COUNT(*)
FROM place_review

	place_name = #{placeName}

		AND region = #{region}

SELECT COALESCE(AVG(rating), 0.0)
FROM place_review

	place_name = #{placeName}

		AND region = #{region}
